#!/usr/bin/env ruby
def next_list_item(match) spaces = match[1] if spaces =~ /	/ spaces = "	" end if match[2] =~ /\*/ "#{spaces}*#{match[4].chomp()}" else "#{spaces}#{match[3].to_i + 1}.#{match[4].chomp()}" end end
listregex = /^(\s*)(\*|([0-9]+)\.)( |	)*/ match = nil lines = $stdin.readlines() lines.reverse.each() do |line| match = listregex.match(line) if match break end end
if !match print "#{lines.join().gsub(/[`$]/, "\\1").chomp}
" else endspacing = lines.join()[/
((\s|
)*)\z/m, 1] print "#{lines.join().gsub(/[$`\]/, '\\\1').sub(/(\s|
)*\z/m, '')}
#{next_list_item(match)}$0
#{endspacing}" end
