dict.string:
# This command works well when you use the TextMate service from a 
# web-log editor like Ecto or MarsEdit.  You can write in markdown
# and then convert back to html before save and close.

Markdown.pl|perl -pe '
	# add id="title" argument to headers
	if(($tag, $title) = /<(h[1-6])>(.*?)<\/\1>/) {
		$_ = $title; y|- /|_|; s|[^a-zA-Z0-9_]||g;
		$_ = "<$tag id=\"" . (lc $_) . "\">$title</$tag>\n";
	}

#	# make empty lines in <pre> a nbsp (WordPress doubles these)
#	if($in_pre && /^$/)	{ $_ = "&nbsp;\n"; }
#	elsif(/<pre>/)		{ $in_pre = 1; }
#	elsif(/<\/pre>/)		{ $in_pre = 0; }

	# convert \ to numeric entity (WordPress swallows these)
	s/\\/\&#x5C;/g;
'
