dict.string:
export TEMPFILE=`mktemp "/tmp/XXXX"`

cat >> $TEMPFILE <<EOF
<html>
<head>
	<meta http-equiv="content-type" content="text/html; charset=utf-8" />
	<title>Markdown preview</title>
	<style type="text/css">
	body {	
		background-color: #eee;
		font-family: "Lucida Grande", Verdana, sans-serif;
		font-size: 11px;
		line-height: 1.6em;
	}
	.contents {	
		background: white;
		font-family: Georgia, serif;
		font-size: 13px;
		border: 1px #888 solid;
		padding: 0 1em;
	}
	</style>
</head>
<body>
<div class="contents">
EOF

"$TM_BUNDLE_PATH/markdown" < $TM_FILEPATH >> $TEMPFILE

cat >> $TEMPFILE <<EOF
</div>
</body>
</html>
EOF

mv $TEMPFILE $TEMPFILE.html
open $TEMPFILE.html
