dict.string:
. "$TM_SUPPORT_PATH/lib/html.sh"

if [[ -z $TM_FILENAME ]]
  then title="Markdown as HTML"
  else title="${TM_FILENAME%.*}"
fi

htmlHeader "$title" '<style type="text/css">
  body {  
    background-color: #eee;
  }
  .contents { 
    background: white;
    font-family: Georgia, serif;
    font-size: 13px;
    border: 1px #888 solid;
    padding: 0 1em;
  }
  </style>'

if [[ -f "$TM_FILEPATH" ]]; then
  echo "<base href='tm-file://${TM_FILEPATH// /%20}'>"
fi

beginTag div 'contents'

# make the header above caret into a link that we scroll down to
${TM_RUBY:-ruby} -e '

  lines = STDIN.read.split("\n");
  n = [ENV["TM_LINE_NUMBER"].to_i, lines.length].min - 1

  while n > 0 && !lines[n].match(/^#+/)
    n = n-1
  end

  if n > 0 && m = lines[n].match(/^(#+\s*)(.*?)(\s*#*)$/)
    lines[n] = "#{m[1]}<span id=\"scroll_to_here\">#{m[2]}</span>#{m[3]}"
  end

  puts lines.join("\n")
  puts "<script>window.location.hash = \"scroll_to_here\";</script>"

'|"$TM_SUPPORT_PATH/bin/Markdown.pl"

endTag
htmlFooter
