dict.string:
. "$TM_SUPPORT_PATH/lib/html.sh"

if [[ -z $TM_FILENAME ]]
  then title="Markdown as HTML"
  else title="${TM_FILENAME%.*}"
fi

htmlHeader "$title" '<style type="text/css">
	body {	
		background-color: #eee;
	}
	.contents {	
		background: white;
		font-family: Georgia, serif;
		font-size: 13px;
		border: 1px #888 solid;
		padding: 0 1em;
	}
	</style>'

if [[ -f ${TM_FILEPATH} ]]; then
	echo "<base href='tm-file://${TM_FILEPATH// /%20}'>"
fi

tmp=$(mktemp /tmp/markdown_heading.XXXXXX)
beginTag div 'contents'
	tee >(head -n$TM_LINE_NUMBER|grep '^#'|tail >"$tmp" -n1)\
	|"$TM_SUPPORT_PATH/bin/Markdown.pl"|perl -pe '
	# make <h1>Header</h1> into <h1 id="header">Header</h1>
	# so that we can link to it
	if(($tag, $title) = /<(h[1-6])>(.*?)<\/\1>/) {
		$_ = $title; s|<[^>]+>||g; y|- /|_|; s|__+|_|g; s|[^a-zA-Z0-9_]||g;
		$_ = "<$tag id=\"" . (lc $_) . "\">$title</$tag>\n";
	}'
endTag

wait; cat <<JS
	<script>
		window.location.hash = "$(perl -pe <"$tmp" 's|^#+ ||; s|<[^>]+>||g; y|- /|_|; s|__+|_|g; s|[^a-zA-Z0-9_]||g; $_ = lc $_')"
	</script>
JS
rm -f "$tmp"

htmlFooter
